import React, { Component } from "react";
import Container from "../Container";
import GameCard from "../GameCard";
import Header from "../Header";
import data from "../../data";


class Game extends Component {

   state = {
       data,
       score: 0,
       topScore: 0,
       message: "Click as many images as you can without repeating"
   };
   componentDidMount() {
      this.setState({ data: this.shuffleDeck(this.state.data) });
  }

  shuffleDeck = data => { 
   let cardArray2 = data.sort(function(a,b){ 
      return 0.5 - Math.random()
   });
   return cardArray2; 
}

resetDeck = data => { 
   const resetData = data.map(item => ({ item, clicked: false})); 
   return this.shuffleDeck(resetData); 
}


correctGuess = cardArray2 => { 
   let newScore = this.state.score;
    newScore++
    let newTopScore = Math.max(newScore, this.state.topScore);

    this.setState({ 
       data:this.shuffleDeck(cardArray2), 
       score: newScore, 
       topScore: newTopScore
    })
}


wrongGuess = cardArray2 => { 
   this.setState({ 
      data: 0, 
      score: 0
   })
   this.shuffledDeck();

}





  cardClicked = id => { 
   let guessedCorrectly = false;
      const cardArray2 = this.state.data.map(item => { 
         if (item.id === id) { 
            if(!item.clicked) {
            item.clicked = true; 
            guessedCorrectly = true;
            }
         }
         return item;
      }); 
      guessedCorrectly ? this.correctGuess(cardArray2) : this.wrongGuess(cardArray2); 

  };









   render() {
       return (
           <div className="animated fadeIn">
               <Header score={this.state.score} topScore = {this.state.topScore} />

               <Container>
                   {
                       this.state.data.map(item => (
                           <div className="animated rollIn">
                               <GameCard
                                   key={item.id}
                                   id={item.id} 
                                   image={item.image}
                                   clicked={item.clicked}
                                   handleClick={this.cardClicked}
                               />      
                           </div>  
                       ))
                   }
               </Container>

           </div>
       );
   }
}

export default Game;
