import React, { Component } from "react";
import Container from "../Container";
import GameCard from "../GameCard";
import Header from "../Header";
import data from "../../data";


class Game extends Component {

   state = {
       data,
       score: 0,
       topScore: 0,
       message: "Click as many images as you can without repeating"
   };

  cardClicked = id => { 
   let guessedCorrectly = false;
      const cardArray2 = this.state.data.map(item => { 
         if (item.id === id) { 
            if(!item.clicked) {
            item.clicked = true; 
            guessedCorrectly = true;
            }
         }
         return item;
      }); 
      guessedCorrectly ? this.correctGuess(cardArray2) : this.wrongGuess(cardArray2); 

  };





   // resets all the clicked properties to false
   resetDeck = data => {
       const resetData = data.map(item => ({ ...item, clicked: false }));
       // console.log(data);
       // console.log(resetData);
       return this.shuffleDeck(resetData);
     };

   // checks to see if score is higher than the topscore then updates the state with the update array
   correctGuess = newData => {
       let newScore = this.state.score;
       newScore++
       let newTopScore = Math.max(newScore, this.state.topScore);

       this.setState({
           data: this.shuffleDeck(newData),
           score: newScore,
           topScore: newTopScore,

       })
   }

   // restarts the game with fresh data
   wrongGuess = newData => {
       this.setState({
           data: this.resetDeck(newData),
           score: 0
       })
   }

   // when a card is clicked, check if it has been clicked before,
   // then update that cards clicked property
   gameCardClick = id => {
       let guessedCorrectly = false;
       // newData will be the data array with updated clicked properties
       const newData = this.state.data.map(item => {
         if (item.id === id) {
           if (!item.clicked) {
             item.clicked = true;
             guessedCorrectly = true;
           }
         }
         return item;     
       });
       // if guessedCorrectly = true, run the correctGuess function,
       // else run the wrongGuess function
       guessedCorrectly ? this.correctGuess(newData) : this.wrongGuess(newData);
     };

   render() {
       return (
           <div className="animated fadeIn">
               <Header score={this.state.score} topScore = {this.state.topScore} />

               <Container>
                   {
                       this.state.data.map(item => (
                           <div className="animated rollIn">
                               <GameCard
                                   key={item.id}
                                   id={item.id} 
                                   image={item.image}
                                   clicked={item.clicked}
                                   handleClick={this.gameCardClick}
                               />      
                           </div>  
                       ))
                   }
               </Container>

           </div>
       );
   }
}

export default Game;
