import React, { Component } from "react";
import Container from "../Container";
import GameCard from "../GameCard";
import Header from "../Header";
import data from "../../data";


class Game extends Component {

   state = {
       data,
       score: 0,
       topScore: 0,
   };

  cardClicked = id => { 
   let guessedCorrectly = false;
      const cardArray2 = this.state.data.map(image => { 
         if (image.id === id) { 
            if(!image.clicked) {
            image.clicked = true; 
            guessedCorrectly = true;
            }
         }
         return image;
      }); 
      guessedCorrectly ? this.correctGuess(cardArray2) : this.wrongGuess(cardArray2); 

  };


  shuffleDeck = data => { 
   let cardArray2 = data.sort(function(a,b){ 
      return 0.5 - Math.random()
   });
   return cardArray2; 
}

resetDeck = data => { 
   const resetData = data.map(image => ({ image, clicked: false})); 
   return this.shuffleDeck(resetData); 
}


correctGuess = cardArray2 => { 
   let newScore = this.state.score;
    newScore++
    let newTopScore = Math.max(newScore, this.state.topScore);

    this.setState({ 
       data: this.shuffleDeck(cardArray2), 
       score: newScore, 
       topScore: newTopScore
    })
}


wrongGuess = cardArray2 => {
   this.setState({
       data: this.resetDeck(cardArray2),
       score: 0
   })
}



   render() {
       return (
           <div className="animated fadeIn">
               <Header score={this.state.score} topScore = {this.state.topScore} 

               />


               <Container>
                   {
                       this.state.data.map(image => (
                           <div >
                               <GameCard
                                   key={image.id}
                                   id={image.id} 
                                   image={image.image}
                                   clicked={image.clicked}
                                   handleClick={this.cardClicked}
                               />      
                           </div>  
                       ))
                   }



               </Container>

           </div>
       );
   }
}

export default Game;
