import React, { Component } from "react";
import Container from "../Container";
import GameCard from "../GameCard";

import Header from "../Header";

import data from "../../data";


class Game extends Component {

    state = {
        data,
        score: 0,
        topScore: 0,
        message: "Click as many images as you can without repeating"
    };

    // you need to tell which card is clicked on and add that card to an array of clicked on cards
    // if user selects a card that is pushed to that array then the game will be over
    // gameover will return that the game is over



    gameOver = () => { 

        if (this.state.score > this.state.topScore) { 
            this.setState({topScore: this.state.score}, function() { 
                console.log(this.state.topScore);
            });
        }
        this.state.data.forEach(card => { 
            card.count = 0; 
        }); 
        alert("Game Over!"); 
        this.setState({score: 0}); 
        return true;
    }

    clickCount = id => {
        this.state.data.find((a, b) => {
          if (a.id === id) {
            if(data[b].count === 0){
              data[b].count = data[b].count + 1;
              this.setState({score : this.state.score + 1}, function(){
                console.log(this.state.score);
              });
              this.state.data.sort(() => Math.random() - 0.5)
              return true; 
            } else {
              this.gameOver();
            }
          }
        });
      }




    render() {
        return (
           <Container>


         <Header score={this.state.score} topScore = {this.state.topScore} />

         {this.state.data.map(card => (
          <GameCard
            clickCount={this.clickCount}
            id={card.id}
            key={card.id}
            image={card.image}
          />
          ))}
            </Container>   
        );
    }
}

export default Game;
